
def merge_the_tools(string, k):
    n=len(string)//k
    distinct_str=dict()   
    list_index_str=[]
    for index in range(k):
        string_index=string[n*index:n*(index+1)]
        for  word in range(65,90):
            if chr(word) in string_index:
                distinct_str[string_index.find(chr(word))]=chr(word)
                list_index_str.append(string_index.find(chr(word)))
        list_index_str.sort()
        for index in list_index_str:
            print(distinct_str[index],end='')
        print()
        list_index_str.clear()
merge_the_tools('',2)        



